package comandapp.comandappcliente.persistencia;

import android.content.Context;
import android.database.sqlite.SQLiteDatabase;
import android.database.sqlite.SQLiteOpenHelper;

/**
 * Created by G62 on 13-Mar-15.
 */
public class SQLHelper extends SQLiteOpenHelper {

    String sqlCreateBar = "CREATE TABLE bar ("+
            "Id_Bar INTEGER,"+
            "Nombre VARCHAR,"+
            "Direccion VARCHAR,"+
            "Telefono VARCHAR,"+
            "Longitud DOUBLE,"+
            "Latitud DOUBLE,"+
            "Provincia VARCHAR,"+
            "Municipio VARCHAR,"+
            "Correo VARCHAR,"+
            "VersionInfoBar INTEGER,"+
            "VersionCarta INTEGER,"+
            "VersionOfertas INTEGER,"+
            "CONSTRAINT pk_Bar PRIMARY KEY(Id_Bar));";

    String sqlCreateCarta = "CREATE TABLE carta ("+
            "Id_Producto INTEGER,"+
            "Id_Bar INTEGER,"+
            "Precio DOUBLE,"+
            "Descripcion VARCHAR,"+
            "Foto VARCHAR,"+
            "CONSTRAINT pk_Carta PRIMARY KEY(Id_Producto,Id_Bar),"+
            "CONSTRAINT fk_Carta_Bar FOREIGN KEY(Id_Bar) REFERENCES bar(Id_Bar));";

    String sqlCreateOferta = "CREATE TABLE oferta ("+
            "Id_Oferta INTEGER,"+
            "Id_Bar INTEGER,"+
            "Precio DOUBLE,"+
            "Descripcion VARCHAR,"+
            "Foto VARCHAR,"+
            "CONSTRAINT pk_Oferta PRIMARY KEY(Id_Oferta),"+
            "CONSTRAINT fk_Oferta_Bar FOREIGN KEY(Id_Bar) REFERENCES bar(Id_Bar));";

    String sqlCreateProducto = "CREATE TABLE producto ("+
            "Id_Producto INTEGER,"+
            "Nombre VARCHAR,"+
            "Categoria VARCHAR,"+
            "CONSTRAINT pk_Producto PRIMARY KEY(Id_Producto));";

    String sqlInsertBar = "INSERT INTO bar (Id_Bar, Nombre, Direccion, Telefono, Latitud, Longitud, Provincia, Municipio, Correo, VersionInfoBar, VersionCarta, VersionOfertas) VALUES" +
            "(1, 'Bar Paco', 'Calle Elefante', 12345678, 5.5, 5.5, 'Málaga', 'Estpona', 'uno@dos.com', 2, 2, 2)," +
            "(2, 'Restaurante Chino', 'Calle Caniche', 654321, 8.8, 8.8, 'Shangay', 'Tai', 'dos@uno.xcom', 1, 0, 5);";

    String sqlInsertCarta = "INSERT INTO carta (Id_Producto, Id_Bar, Precio, Descripcion, Foto) VALUES" +
            "(2, 1, 1.5, 'CocaCola Lata 33 cl', '')," +
            "(3, 1, 1.5, 'CocaCola 1 L', '');";

    String sqlInsertOferta = "INSERT INTO oferta (Id_Oferta, Id_Bar, Precio, Descripcion, Foto) VALUES" +
            "(1, 1, 1, '1,2', '');";

    String sqlInsertaProducto = "INSERT INTO producto (Id_Producto, Nombre, Categoria) VALUES" +
            "(1, 'Huevos', 'A')," +
            "(2, 'CocaCola Lata', 'B')," +
            "(3, 'CocaCola 1L', 'B');";

    public SQLHelper(Context contexto, String nombre, SQLiteDatabase.CursorFactory factory,
                       int version) {
        super(contexto, nombre, factory, version);
        // TODO Auto-generated constructor stub
    }

    @Override
    public void onCreate(SQLiteDatabase db) {
        //Se ejecuta la sentencia SQL de creaci�n de la tabla
        db.execSQL(sqlCreateBar);
        db.execSQL(sqlCreateCarta);
        db.execSQL(sqlCreateOferta);
        db.execSQL(sqlCreateProducto);

        db.execSQL(sqlInsertBar);
        db.execSQL(sqlInsertCarta);
        db.execSQL(sqlInsertOferta);
        db.execSQL(sqlInsertaProducto);
    }

    @Override
    public void onUpgrade(SQLiteDatabase db, int versionAnterior, int versionNueva) {
        //Por ahora nada
    }

}
